scan_map = function scan_map(geo_data, value, primary_color, colors, title, source) {

  return bertin.draw({
    params: {
      extent: geo_data,
      margin: 30,
      background: "#bde1f0"
    },
    layers: [
      {
        type: "header",
        text: title,
        background: primary_color,
        fill: "white",
        fontSize: 40,
        anchor: "start"
      },
     //{
     //  type: "footer",
     //  text: `SCAN360, 2023
     //  Data: Florida Cancer Data System; Calculations SCAN360`
     //},
      {
        type: "layer",
        geojson: geo_data,
        strokeWidth: 1.5,
        fill: {
          type: "choro",
          values: value,
          nbreaks: 5,
          //breaks: [1, 2, 3],
          method: "equal",
          colors: colors,
          leg_x: 180,
          leg_y: 550,
          leg_w: 45,
          leg_h: 30,
          leg_round: 1,
          leg_title: "Age-Adjusted Rate per 100.000",
          leg_fontSize: 20,
          leg_fontSize2: 14,
      },

      tooltip: {
          fields: [
            `$${value}`,
            "per 100,000",
            "Age-Adjusted Rate",
            "- - - - - - - - - - - - - ",
            "$NAMELSAD"
          ],
          fill: "#ffffff",
          stroke: primary_color,
          strokeWidth: 3,
          fillOpacity: 0.85,
          strokeOpacity: 1,
          fontWeight: ["bold", "lighter", "lighter", "lighter", "bold"],
          fontSize: [50, 16, 20, 25, 30],
          fontStyle: ["normal", "normal", "normal", "normal", "bold"],
          col: primary_color
      }
        },
      { type: "shadow", geojson: geo_data, dx: 5, dy: 5 },
     // {
     //   type: "layer",
     //   geojson: us,
     //   fill: "#e0d0b6",
     // },
      { type: "scalebar", units: "miles" }
    ]
  })
}
